script:
frappe.ui.form.on('Collection In Hand', {
    before_save(frm) {
        if (frm.doc.amount > 0) {
            frm.set_value('amount', -Math.abs(frm.doc.amount));
        }
    }
});

frappe.ui.form.on("Loan Group Assignment", {
    onload: function(frm) {
        frm.set_query("loan_group", function() {
            return {
                filters: {
                    workflow_state: "Approved"
                }
            };
        });
    }
});

frappe.ui.form.on("Loan Member", {
    onload: function(frm) {
        frm.set_query("group", function() {
            return {
                filters: {
                    workflow_state: "Approved"
                }
            };
        });
    }
});
